import { useState } from "react";
import axios from "axios";
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

function AddProPop({category}) {
    const [product, setProduct] = useState("");
    // const [category, setCategory] = useState([]);
    // const [selcat, setSelcat] = useState("");
    // const [error, setError] = useState("");
    const defaultCategory = category[0];

    const handleSubmit = (e) => {

    }

    return (
        <Popup trigger={<button> Add Product</button>} position="top center">
            <div>
                {category.map((cat) => {
                    return (
                        <p>{cat.category}</p>
                    )
                })}
                <form onSubmit={handleSubmit}>
                <Dropdown options={category} onChange={this._onSelect} value={defaultCategory} placeholder="Select a Category" />;
                    <input
                        type="text"
                        placeholder="Enter new Product"
                        name="Product"
                        onChange={({ currentTarget: input }) => {
                            setProduct(input.value);
                            console.log(product)
                        }}
                        value={product}
                        required
                    // className={styles.input}
                    />
                    <button type="submit">
                        Add
                    </button>
                </form>
            </div>
        </Popup>
    )
}

export default AddProPop
