import { useEffect, useState } from 'react';
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import styles from "./styles.module.css";
import { Fragment } from "react/cjs/react.production.min";


function ForgotPass() {

	const [validUrl, setValidUrl] = useState(true);
	const param = useParams();

	const [data, setData] = useState({ password: "", rpassword: "" });
	const [error, setError] = useState("");

	const handleChange = ({ currentTarget: input }) => {
		setData({ ...data, [input.name]: input.value });
		console.log(data);

	};

	useEffect(() => {
		const verifyEmailUrl = async () => {
			try {
				const url = `http://localhost:8080/api/users/${param.id}/resetpass/${param.token}`;
				con
				const { data } = await axios.get(url);
				console.log(data);
				setValidUrl(true);
			} catch (error) {
				console.log(error);
				setValidUrl(false);
			}
		};
		verifyEmailUrl();
	}, [param]);

	const handleSubmit = async (e) => {
	};

	return (
		<div>
			<Fragment>
				{validUrl ? (<form className={styles.form_container} onSubmit={handleSubmit}>
					<h1>Reset Password</h1>
					<input
						type="password"
						placeholder="password"
						name="password"
						onChange={handleChange}
						value={data.password}
						required
						className={styles.input}
					/>
					<input
						type="password"
						placeholder="Re-Enter Password"
						name="rpassword"
						onChange={handleChange}
						value={data.rpassword}
						required
						className={styles.input}
					/>
					{error && <div className={styles.error_msg}>{error}</div>}
					<button type="submit" className={styles.green_btn}>
						Confirm New Password
					</button>
				</form>) : (
					<h1>404 Not Found</h1>
				)}</Fragment>)
		</div>
	)
}

export default ForgotPass;
