import React from 'react'
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

function addCatPop() {
    return (
        <Popup trigger={<button> Add Category</button>} position="right center">
            <div>
            <form onSubmit={handleSubmit}>
						<h1 className="welcome">Welcome Back !</h1>
						<input
							type="email"
							placeholder="Email"
							name="email"
							onChange={handleChange}
							value={data.email}
							required
							className={styles.input}
						/>
						<input
							type="password"
							placeholder="Password"
							name="password"
							onChange={handleChange}
							value={data.password}
							required
							className={styles.input}
						/>
						{error && <div className={styles.error_msg}>{error}</div>}
						<button type="submit">
							Log In
						</button>
						<GoogleLogin style={{ width: "200px" }} className="googleLogin"
							clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID}
							buttonText="Log In with Google"
							onSuccess={handleLogin}
							onFailure={handleFailure}
							cookiePolicy={'single_host_origin'}
						/>
						<div className="goToSign">
							<p>New Here?
								<a style={{ margin: "5px", textDecoration: "none", color: "#3300FF" }} href="/register">
									<b>Sign Up</b>
								</a>
							</p>
						</div>
						<a className="forgotPass" href="/for">Reset Password</a>
					</form>
            </div>
        </Popup>
    )
}

export default addCatPop
